const math = require('mathjs');

const generate = (usePredefinedValues = 0) => {
    // Error handling around math operations and input validation
    try {
        // Dynamic Parameter Selection with flexible unit system
        const unitSystems = {
            si: {
                speed: 'km/h',
                distance: 'km',
                time: 'hr'
            },
            uscs: {
                speed: 'mph',
                distance: 'mi',
                time: 'hr'
            }
        };

        // Select a unit system based on predefined values or dynamic selection
        const selectedSystem = usePredefinedValues ? 'si' : (math.random() > 0.5 ? 'si': 'uscs');
        const units = unitSystems[selectedSystem];

        // Value Generation ensuring consistency with the question requirements
        let v, t;
        if (usePredefinedValues) {
            v = selectedSystem === 'si' ? 160 : 100; // 160 km/h or 100 mph as predefined values
            t = 5; // Fixed to 5 hours
        } else {
            // Generate random value ranges for realism and consistency with appropriate checks
            v = selectedSystem === 'si' ? math.randomInt(80, 181) : math.randomInt(50, 101); // Random speed value based on unit system
            t = math.random(1, 10); // Random time between 1 and 10 hours

            if (!Number.isFinite(v) || v <= 0) {
                throw new Error("Generated speed is not in a valid range.");
            }
            if (!Number.isFinite(t) || t <= 0) {
                throw new Error("Generated time is not in a valid range.");
            }
        }

        // Computation Logic applying deterministic equations
        const d = v * t; // total distance = speed * time

        return {
            params: {
                speed: v,
                time: t,
                unitsSpeed: units.speed,
                unitsTime: units.time,
                unitsDistance: units.distance
            },
            correct_answers: {
                distance: math.round(d, 3)
            },
            intermediate: {
                system: selectedSystem,
                computedDistance: d
            },
            nDigits: 3,
            sigfigs: 3
        };
    } catch (error) {
        console.error("Error generating values: ", error.message);
        return {
            params: {},
            correct_answers: {},
            intermediate: {},
            nDigits: 3,
            sigfigs: 3,
            error: error.message
        };
    }
};

module.exports = { generate };

