const math = require("mathjs");

const generate = (usePredefinedValues = 0) => {
    // 1. Dynamic Parameter Selection
    const unitsSpeed = "mph";
    const unitsTime = "hours";
    const unitsDistance = "miles";

    // 2. Value Generation
    // If usePredefinedValues === 0, generate realistic random values
    // If usePredefinedValues === 1, use predefined values
    let v, t;
    if (usePredefinedValues === 0) {
        v = 100; // mph
        t = 5;   // hours
    } else {
        v = 60; // predefined value for internal testing
        t = 3;  // predefined value for internal testing
    }

    // 3. Computation Logic
    const d = math.multiply(v, t); // Compute distance as d = vt

    return {
        params: {
            v,
            t,
            unitsSpeed,
            unitsTime,
            unitsDistance
        },
        correct_answers: {
            d: math.round(d, 3) // Round to 3 significant figures
        },
        intermediate: {},
        nDigits: 3,
        sigfigs: 3
    };
};

module.exports = { generate };
