const math = require("mathjs");

const generate = (usePredefinedValues = 0) => {
    // 1. Dynamic Parameter Selection
    const speedUnit = "mph"; // Using miles per hour as unit
    const timeUnit = "hours"; // Using hours as time unit

    // 2. Value Generation
    let speed, time;

    if (usePredefinedValues === 0) {
        speed = 100; // Constant speed in mph for generic calculation
        time = 5; // Total time in hours for generic calculation
    } else {
        speed = 100; // Predefined speed value for testing
        time = 5; // Predefined time value for testing
    }

    // 3. Computation Logic
    // Total distance = speed * time
    const distance = math.multiply(speed, time);

    return {
        params: {
            speed: speed,
            time: time,
            speedUnit: speedUnit,
            timeUnit: timeUnit
        },
        correct_answers: {
            distance: distance
        },
        intermediate: {
            distanceFormula: "distance = speed * time"
        },
        nDigits: 3,
        sigfigs: 3
    };
};

module.exports = { generate };
